ejercicios = [{"denominacion": "No hay respiro", "descripcion": "Buscamos llegar a tres pases en el equipo para devolver el balón al sacador de banda = 2 ptos.\nEste devolverá el balón al equipo pasador = 1 pto.\n(2x2, 3x3, 4x4...+1)\nVariante: logrados 3 pases en el equipo al pasar al sacador intercambio de funciones.", "estrategia": ["Siempre en movimiento.", "Acción para el otro.", "Dos actitudes en defensa a descubrir ante diversas valoraciones en el jugar.", "Enriquecer con cortes y su defensa. Chocar los cortes."], "duracion": 20}, {"denominacion": "Mensaje", "descripcion": "Intentamos pasar el balón a un jugador defendido dentro del círculo.\nSuperioridad atacante.", "estrategia": ["Desmarcarse y buscar línea de pase.", "Sellar el apoyo fuerte del defensor.", "Fijar al defensor y dar referencia."], "duracion": 20}, {"denominacion": "Balón-azúcar", "descripcion": "4x1. Quien la para sólo puede pillar al jugador que no está en posesión de balón.\nAcotar el terreno de juego.", "estrategia": ["Anticipar.", "Fintar", "Apoyar la línea de pase.", "Crear exigencias antes de poder pasar y delimitar espacios."], "duracion": 10}, {"denominacion": "Pasar por aro", "descripcion": "Al recibir el balón cruzamos el círculo driblando y nos desocupamos del balón sin ser tocado.", "estrategia": ["¿Dónde puedo recibir el balón?", "¿Cuándo decidirse a cruzar?"], "duracion": 20}, {"denominacion": "Entrar con llave", "descripcion": "Pasar al recién cambiado que llega al centro con balón.\nNecesitamos 1 balón y los aros.", "estrategia": ["Siempre en movimiento.", "Acción para el otro.", "Dos actitudes en defensa a descubrir ante diversas valoraciones en el jugar.", "Enriquecer con cortes y su defensa. Chocar los cortes."], "duracion": 10}, {"denominacion": "Sin parar", "descripcion": "4x4, 5x5\nPase al jugador interior e intercambio de posiciones.", "estrategia": ["Interior / Exterior.", "Reemplazar.", "Ganar la entrada del balón en el juego interior.", "Pasar y seguir."], "duracion": 10}, {"denominacion": "Las alianzas", "descripcion": "4 x 2 + 2.\n4 atacantes intentan tocar con el balón adaptado a 2.\nDos defensores se lo impiden.", "estrategia": ["Intercepciones de pases.", "Buscarse apoyos"], "duracion": 10}, {"denominacion": "Vuelvete loco", "descripcion": "Dos parejas se pasan y mueven defendidas por uno en cancha.\nDos balones a la vez.", "estrategia": ["Amplitud visual.", "Variedad de pases", "Exigencias."], "duracion": 10}, {"denominacion": "Interceptar", "descripcion": "Pasar y cortar.\nDefender saltando al balón.", "estrategia": ["Cortar por delante / detrás.", "Cambio de dirección."], "duracion": 20}, {"denominacion": "Enjaulados", "descripcion": "2 x 1. Los defensores no pueden sacar los dos pies de los círculos.\nFacilitar el ataque acotando los movimientos de los defensores.\nApoyar la defensa creando exigencias nuevas en los pases.", "estrategia": ["Negar las líneas de pase.", "Analizar los pases y las recepciones en carrera.", "Fintas de pase y puertas atrás"], "duracion": 10}, {"denominacion": "Transmisión difícil", "descripcion": "Buscamos la diversidad en los pases en unos espacios fijos y defendidos.\n4 x 4. La recepción entre tus pivotes.\nInicio de la defensa de espacios: Zonas.", "estrategia": ["Fintas de pase.", "Puntos por donde pasar.", "Diversidad en el pase."], "duracion": 20}, {"denominacion": "No al vecino", "descripcion": "6 x 1 con dos balones\n7 x 3 y un balón\nNo podemos pasar el balón al compañero vecino.", "estrategia": ["Visión periférica.", "Elección de pase.", "Selección de pases.", "Fintas defensivas y engaños de pase."], "duracion": 10}, {"denominacion": "El bloqueador", "descripcion": "Relevo de un lado a otro por el ancho del campo y en medio, libres de movimiento, encontramos un compañero que botando nos facilita el paso y otro que botando nos corta la trayectoria.", "estrategia": ["Lectura de bloqueos.", "Saltarlos.", "Visión periférica.", "Bote con ambas manos; buen manejo"], "duracion": 30}, {"denominacion": "Pareja paradora", "descripcion": "Numerados por parejas nos pasamos el balón. Cuando nos nombran pasamos el balón al entrenador y nos dedicamos a interceptar los pases del resto de las parejas.", "estrategia": ["Interior / Exterior.", "Reemplazar.", "Ganar la entrada del balón en el juego interior.", "Pasar y seguir."], "duracion": 20}, {"denominacion": "Los intocables", "descripcion": "4 x 5. El quinteto defensor intercepta: ni tocan ni desposeen.\nBuscamos los 2 x 1 y un sentido de presión.", "estrategia": ["Dos al balón.", "No telegrafiamos los pases."], "duracion": 10}, {"denominacion": "Dobla en defensa", "descripcion": "Salir a defender al oponente con balón y disuadir con dos defensores. 5 x 3.\nUn atacante dentro del círculo y cuatro en las esquinas.\nPivotes de circulaciones y relevos.", "estrategia": ["Primar un tipo de pases.", "Fomentar por el valor de las acciones un tipo de defensa.", "Movilidad.", "Sentido de posesión-control."], "duracion": 20}, {"denominacion": "Riesgo y protección", "descripcion": "Pases fuera y dentro; dentro-dentro; dentro y fuera.\nTres pases dentro = 1 punto.\nDos equipos dentro del espacio de juego y uno por fuera de apoyo.\nAl lograr el tercer pase nos apoyamos en el equipo exterior e intercambiamos posición.", "estrategia": ["Primar un tipo de pases.", "Fomentar por el valor de las acciones un tipo de defensa.", "Movilidad.", "Sentido de posesión-control."], "duracion": 20}, {"denominacion": "Más dificil", "descripcion": "4 x 3 buscan canasta. Cobran punto cada vez que logran su propósito en un número corto de acciones.\nSuperado el número, cambio de posesión.\nToda la cancha", "estrategia": ["Ver huecos.", "Cambios de ritmo.", "Cambios de dirección."], "duracion": 10}, {"denominacion": "Un cañón por banda", "descripcion": "4 jugadores con balón, intentan cruzar por una zona restringida por conos y defendida por dos.", "estrategia": ["Ver huecos.", "Cambios de ritmo.", "Cambios de dirección."], "duracion": 30}, {"denominacion": "Cogidos de a dos", "descripcion": "Parejas cogidos de la mano nos movemos persiguiendo.\nUno dribla el balón y lo compartimos.\nJugadores sin pareja botando el balón y que para ser librados del acoso agarran la mano libre de las parejas; el otro con balón se larga y peligra.", "estrategia": ["Bote dificultado.", "Vista amplia.", "Sentido de espacio."], "duracion": 20}, {"denominacion": "Laberinto", "descripcion": "A persigue a B favorecido por los brazos que cambian de agarre creando diversos pasillos botando estáticos (en aro, en neumático,...)", "estrategia": ["Discriminar la ayuda y la protección.", "Atender los cambios."], "duracion": 10}, {"denominacion": "Dos en apuros", "descripcion": "Dos botando quieren juntarse y el defensor no les deja.\nUsar claves de números, colores,... buscando el retener, el centrarse.", "estrategia": ["Defensa activa y toma de decisiones.", "Pelea por el balón.", "Fintas en bote.", "Memoria y atención."], "duracion": 10}, {"denominacion": "Balón repelido", "descripcion": "En una zona reducida de 2 x 4 metros A y B se pasan el balón evitando ser tocados en posesión.", "estrategia": ["Palmeos.", "Fintas de pase.", "Sujeción del balón."], "duracion": 30}, {"denominacion": "Ayuda", "descripcion": "Aguantando el balón en 1 x 1 pasamos a un tercer jugador y saltamos a iniciar el nuevo 1 x 1.", "estrategia": ["Protección del balón.", "Flexibilidad de acciones.", "Sentir la defensa y sellar su paso."], "duracion": 10}, {"denominacion": "Persecución", "descripcion": "Las parejas evolucionan por la cancha pasándose el balón. A la señal quien esté en posesión del balón buscará meter canasta. El otro defenderá.", "estrategia": ["Repaso de pases.", "Calidad y distancia.", "Espacio restringido/abierto/colisión"], "duracion": 10}, {"denominacion": "Waterloo", "descripcion": "Dos equipos cambian de lado botando sus componentes cada uno su balón y venciendo la oposición de un tercer equipo que se mueve por el espacio entre lado y lado.\n1 punto por equipo que primero se reúne.\nLogrados x puntos el vencedor cambia al medio.", "estrategia": ["Visión en el bote.", "Atacar botando contra todos.", "Pelea y dificulta el logro de los demás.", "Intensidad al fijar al atacante"], "duracion": 10}, {"denominacion": "Pasar y seguir", "descripcion": "1 x 1 desde media cancha y con dos postes repetidores.", "estrategia": ["Pasar y seguir.", "Negar el pase de vuelta"], "duracion": 10}, {"denominacion": "Seis conquistas", "descripcion": "Dividida la zona en sub-espacios buscamos con la colaboración de un pasador el recibir en cada una de ellas.", "estrategia": ["Memoria.", "Lucha por recibir."], "duracion": 10}, {"denominacion": "Pato mareado en movimiento", "descripcion": "4 x 1 se mueven pasándose el balón y siempre recibiendo en movimiento.", "estrategia": ["Coordinación.", "Gran apoyo de pases.", "Tesón defensivo.", "Exigencias."], "duracion": 20}, {"denominacion": "Balón suicida", "descripcion": "En media cancha 5 x 1. El que pilla busca tocar al jugador con balón que aguantará al bote o le exigiremos acciones antes de pasar.", "estrategia": ["Recibir en movimiento.", "Anticipar."], "duracion": 10}, {"denominacion": "El zorro, la gallina y los polluelos", "descripcion": "El zorro, la gallina y el polluelo último llevan balón al bote.\nEl zorro trata de tocar al último polluelo.", "estrategia": ["Dominios del bote.", "Extrema colaboración.", "Estrategias de ruptura del grupo familiar."], "duracion": 10}, {"denominacion": "Sardana atrapo", "descripcion": "4 cogidos de la mano protegen al ratón de ser pillado por el gato que juegan botando cada uno su balón.", "estrategia": ["Buscamos el trabajo de pies en la defensa.", "Todos pelean todo.", "Cambios bien continuados y organizados."], "duracion": 10}, {"denominacion": "Ring", "descripcion": "El jugador en posesión del balón debe evitar ser tocado por el defensor. Lo evita al dejar el balón en el suelo y salir a tocar la línea de banda. El defensor copiará sus movimientos.", "estrategia": ["Visión.", "Anticipación", "Engaños.", "Cambios de ritmo."], "duracion": 10}]
